create database boot_crm;
use boot_crm;
create table base_dict
(
	dict_id varchar(32) primary key not null, -- 数据字典id
	dict_type_code varchar(10) not null,      -- 数据字典类别代码
	dict_type_name varchar(50) not null,      -- 数据字典类别名称
	dict_item_name varchar(50) not null,      -- 数据字典项目名称
	dict_item_code varchar(10) default null,      -- 数据字典项目代码(可为空)
	dict_sort int(10) default null,               -- 排序字段
	dict_enable char(1) not null,             -- 是否可用：1：使用 0：停用
	dict_memo varchar(100) default null           -- 备注
)engine=InnoDB default charset=utf8;

