create table customer
(
	cust_id int(32) primary key auto_increment,   -- 客户编号
	cust_name varchar(50) not null,         	-- 客户名称
	cust_user_id int(32) default null,          -- 负责人id
	cust_create_id int(32) default null,        -- 创建人id
	cust_source varchar(50) default null,       -- 客户信息来源
	cust_industry varchar(50) default null,     -- 客户所属行业
	cust_level varchar(32) default null,        -- 客户级别
	cust_linkman varchar(50) default null,      -- 联系人
	cust_phone varchar(64) default null,        -- 固定电话
	cust_mobile varchar(16) default null,       -- 移动电话
	cust_zipcode varchar(10) default null,      -- 邮政编码
	cust_address varchar(100) default null,     -- 联系地址
	cust_createtime datetime  default null      -- 创建时间
)engine=innoDB auto_increment=15 default charset=utf8;
