create table sys_user
(
	user_id int(32) primary key not null auto_increment,                               -- 用户id
	user_code varchar(20) not null,                                     -- 用户账号
	user_name varchar(20) not null,                                     -- 用户名称
	user_password varchar(32) not null,                                 -- 用户密码
	user_state int(1) check(user_state=0 or user_state=1)  -- 用户状态
)engine=innoDB auto_increment=5 default charset=utf8;
